import React, { Component } from 'react';

export class People extends Component {
  //static displayName = Counter.name;

  constructor(props) {
    super(props);
    this.state = { currentRoomCount: 0, currentAdultCount:0, currentChildCount:0 };
    this.incrementCounter = this.incrementCounter.bind(this);
    this.decreamentCounter = this.decreamentCounter.bind(this);
  }

  incrementCounter(type) {
    this.setState({
        currentRoomCount: this.state.currentRoomCount + 1
    });
  }
    decreamentCounter(id) {
        this.setState({
            currentRoomCount: this.state.currentRoomCount + 1
        });
    }

  render() {
    return (
      <div>
        <h1>People Alpha-Test</h1>
<table>
    <tr>
        <td><strong>Rooms</strong></td>
        <td>
            <button className="btn btn-primary" onClick={this.decreamentCounter}>-</button>
    {''}{this.state.currentRoomCount}
    <button className="btn btn-primary" onClick={this.incrementCounter}>+</button>
            </td>
    </tr>
    <tr>
        <td><strong>Adults</strong></td>
        <td>
            <button className="btn btn-primary" onClick={this.decreamentCounter}>-</button>
    {''}{this.state.currentAdultCount}
    <button className="btn btn-primary" onClick={this.incrementCounter}>+</button>
            </td>
    </tr>
    <tr>
        <td><strong>Childs</strong></td>
        <td>
            <button className="btn btn-primary" onClick={this.decreamentCounter}>-</button>
    {''}{this.state.currentChildCount}
    <button className="btn btn-primary" onClick={this.incrementCounter}>+</button>
            </td>
    </tr>
    
</table>
        {/* <p>This is a simple example of a React component.</p>

            <p aria-live="polite">Current count: <strong>{this.state.currentCount}</strong></p>
            <input type="text" value={this.state.currentCount} />

            <button className="btn btn-primary" onClick={this.decreamentCounter}>Decrement</button>
        <button className="btn btn-primary" onClick={this.incrementCounter}>Increment</button> */}
      </div>
    );
  }
}
export default People;
