import React, { Component } from 'react';
import './App.css';
import People from './components/People';
class App extends Component {
  state =
    {
      peoples: [
        {
          id: 1,
          name: 'Rooms',
          currentCount: 1,
          isDisabled: false
        },
        {
          id: 2,
          name: 'Adults',
          currentCount: 0,
          isDisabled: false
        },
        {
          id: 3,
          name: 'Childs',
          currentCount: 0,
          isDisabled: false
        }]
    };
  // Toggle Complete
  decrementCounter = (id) => {
    this.setState({
      peoples: this.state.peoples.map((people) => {
        if (people.id === id) {
          if (people.id === 1)// this is a room
          {
            if (people.currentCount <= 1) {
              alert("Value must be greater than 1!!! ");
            }
            else {
              people.currentCount -= 1;
            }
          }
          else {
            // For adults or Childrens
          }

        }
        return people;
      })

    });
  };
  checkVal(val) {
    if (val === 1) {
      return 4;
    } else if (val === 2) {
      return 3;
    } else if (val === 3) {
      return 2;
    } else if (val === 4) {
      return 1;
    }
    // } else return 4;
  };
  increamentCounter = (id) => {
    this.setState({
      peoples: this.state.peoples.map((people) => {
        var aCount = this.state.peoples[1].currentCount;
        var cCount = this.state.peoples[2].currentCount;
        var rCount = this.state.peoples[0].currentCount;
        if (people.id === 1) {
          rCount = people.currentCount;
        }
        else if (people.id === 2) {
          aCount = people.currentCount;
        }
        else if (people.id === 3) {
          cCount = people.currentCount;
        }

        if (people.id === id) {
          if (people.id === 1)// this is a room
          {
            if (people.currentCount >= 5) {
              alert("Value must be less than 5!!! ");
            }
            else {
              people.currentCount += 1;
            }
          }
          if (people.id === 2)// this is a adults
          {
            if (rCount === 1) {
              var ParamCheck = people.currentCount + cCount;
              if (ParamCheck >= 4) {
                alert("Value must be less than 4");
              }
              else if ((people.currentCount <= this.checkVal(cCount)) && people.currentCount<=4) {
                people.currentCount += 1;
              }
            }
            else {
              people.currentCount += 1;
            }
          }

          if (people.id === 3)// this is a child
          {
            if (rCount === 1) {
              var ParamCheck = people.currentCount + aCount;
              if (ParamCheck >= 4) {
                alert("Value must be less than 4");
              }
              else if ((people.currentCount <= this.checkVal(aCount)) && people.currentCount<=4) {
                people.currentCount += 1;
              }
            }
            else {
              people.currentCount += 1;
            }
          }

        }
        return people;
      })

    });
  };

  checkPassedVal = (val) => {
    if (val === 1) {
      return 1;
    } else if (val === 2) {
      return 2;
    } else if (val === 3) {
      return 3;
    } else {
      return val;
    }
  };
  render() {
    return (
      <div className='App'>
        <div className='container'>

          <People
            peoples={this.state.peoples}
            decrementCounter={this.decrementCounter}
            increamentCounter={this.increamentCounter}
          />

        </div>
      </div>
    );
  }
}
export default App;
