      count = 4;
      count = count * i;
    }
    return count;
  }

  checkVal(val) {
    if (val === 1) {
      return 4;
    } else if (val === 2) {
      return 3;
    } else if (val === 3) {
      return 2;
    } else if (val === 4) {
      return 1;
    } else if (val === 0) {
      return 4;
    } else if (val >= 4) {
      return 0;
    }
  };
