import React, { Component } from 'react';
import PropTypes from 'prop-types';

export class PeopleItem extends Component {
    //static displayName = Counter.name;

    constructor(props) {
        super(props);
       // this.state = {idname: 'Rooms', currentCount: 0 };
        
        //this.incrementCounter = this.incrementCounter.bind(this);
        //this.decreamentCounter = this.decreamentCounter.bind(this);
        // currentRoomCount: 0, currentAdultCount:0, currentChildCount:0 }; 
    }


    render() {
        const{ id,
            name,
            currentCount} = this.props.people;
        return (
           
                <tr>
                                <td><strong>{name}</strong></td>
                                <td>
                                    <button className="btn btn-primary" decrementCounter={this.props.decrementCounter.bind(this, id)}>-</button>
                                    {currentCount}
                                    <button className="btn btn-primary" increamentCounter={this.props.increamentCounter.bind(this,id)}>+</button>
                                </td>
                            </tr>
          
        );
    };
}
PeopleItem.propTypes = {
    people: PropTypes.array.isRequired,
    decrementCounter: PropTypes.func.isRequired,
    incrementCounter: PropTypes.func.isRequired
    
  }
export default PeopleItem;
