import React, { Component } from 'react';
import './App.css';
import People from './components/People';

class App extends Component {
  state =
    {
      peoples: [
        {
          id: 1,
          name: 'Rooms',
          currentCount: 0
        },
        {
          id: 2,
          name: 'Adults',
          currentCount: 0
        },
        {
          id: 3,
          name: 'Childs',
          currentCount: 0
        }]
    };



  // Toggle Complete
  decrementCounter = (id) => {
    this.setState({
      peoples: this.state.peoples.map((people) => {
        // if (todo.id === id) {
        // 	todo.completed = !todo.completed;
        // }
        // return todo;
        alert(people.id);
      })
    });
  };

  increamentCounter = (id) => {
    this.setState({
      peoples: this.state.peoples.map((people) => {
        // if (todo.id === id) {
        // 	todo.completed = !todo.completed;
        // }
        // return todo;
        alert(people.id);
      })
    });
  };

  render() {
    return (
      <div className='App'>
        <div className='container'>
          <People peoples={this.state.peoples} decrementCounter={this.decrementCounter} increamentCounter={this.increamentCounter} />

        </div>
      </div>

    );
  }
}

export default App;
